import React from "react";
import { NavLink } from "react-router-dom";
import logo from "../../images/logo.svg";
import "./WindowWithForm.css";

export default function WindowWithForm({ heading, btnText, question, link, linkText, children }) {
  return (
    <section className="window-form">
      <NavLink to={"/"}><img src={logo} alt="Логотип сайта про диплом" className="window-form__logo link" /></NavLink>
      <h2 className="window-form__title">{heading}</h2>
      <form action="" className="window-form__form">
        <div className="window-form__fieldset">
          {children}
        </div>
        <button className="window-form__form-btn btn">{btnText}</button>
      </form>
      <div className="window-from__link-element">
        <p className="window-from__question">{question}</p>
        <NavLink to={link} className="window-form__link">{linkText}</NavLink>
      </div>
    </section>
  )
}
