import React from "react";

// {name, duration, imgUrl}
export default function MoviesCard({ card: {nameRu, duration, imgUrl} }) {

  function convertDurationToHours(duration) {
    if (typeof duration !== 'number' || duration < 0) {
      return 'Некорректное значение минут';
    }

    const hours = Math.floor(duration / 60);
    const remainingMinutes = duration % 60;

    const formattedHours = hours.toString();
    const formattedMinutes = remainingMinutes.toString().padStart(2, '0');

    return `${formattedHours} часов ${formattedMinutes} минут`;
  }

  let isSavedMovie = false;
  let isRemoveMovies = false;
  return (
    <li className="movies-card">
      <div className="movies-card__info">
        <h2 className="movies-card__title">{nameRu}</h2>
        <p className="movies-card__duration">{convertDurationToHours(duration)}</p>
      </div>
      <img src={imgUrl} alt="Обложка фильма" className="movies-card__image" />
      <button className={`movies-card__btn ${isSavedMovie ? 'movies-card__btn_active' : isRemoveMovies ? 'movies-card__btn_remove' : ''}`} type="button"></button>
    </li>
  )
}
