import tumbOn from "../../images/smalltumb.svg";
import tumbOff from "../../images/smalltumboff.svg"

export default function FilterCheckbox(isActiveCheckbox) {

  return (
    <section className="filter-checkbox">
      {isActiveCheckbox ? (<img alt="" src={tumbOn} className="filter-checkbox__tumb-on"/>) : (<img img alt="" src={tumbOff} className="filter-checkbox__tumb-off"/>) }
      <h2 className="filter-checkbox__title">Короткометражки</h2>
    </section>
  )
}
