import React from "react";

// {name, duration, imgUrl}
export default function MoviesCard() {

  let name = 'В погоне за Бенкси';
  let duration = '0ч 42м';
  let imgUrl = "https://media.istockphoto.com/id/1355176914/ru/%D1%84%D0%BE%D1%82%D0%BE/%D0%BA%D0%B8%D0%BD%D0%BE%D1%82%D0%B5%D0%B0%D1%82%D1%80-%D0%B2%D0%BE-%D0%B2%D1%80%D0%B5%D0%BC%D1%8F-%D0%BF%D0%BE%D0%BA%D0%B0%D0%B7%D0%B0-%D0%B0%D0%BD%D0%B8%D0%BC%D0%B0%D1%86%D0%B8%D0%BE%D0%BD%D0%BD%D0%BE%D0%B3%D0%BE-%D1%84%D0%B8%D0%BB%D1%8C%D0%BC%D0%B0.jpg?s=612x612&w=0&k=20&c=eE2myIrLXwV0rZPLIPwdXUWZUVI2fWhgVyF7Xn9gQgs="

  let isSavedMovie = false;
  let isRemoveMovies = true;
  return (
    <li className="movies-card">
      <div className="movies-card__info">
        <h2 className="movies-card__title">{name}</h2>
        <p className="movies-card__duration">{duration}</p>
      </div>
      <img src={imgUrl} alt="Обложка фильма" className="movies-card__image" />
      <button className={`movies-card__btn ${isSavedMovie && 'movies-card__btn_active'} || ${isRemoveMovies && 'movies-card__btn_active'} `} type="button" ></button>
    </li>
  )
}
