import React from "react";


export default function MoviesCard({name, duration, imgUrl}) {
  return (
    <div className="movies-card">
      <div className="movies-card__info">
        <h2 className="movies-card__title">{name}</h2>
        <p className="movies-card__duration">{duration}</p>
      </div>
      <img src={imgUrl} alt="Обложка фильма" className="movies-card__image" />
      <button className="movies-card__btn" type="button" ></button>
    </div>
  )
}

