import React from "react";

export default function MoviesCard({ card: {nameRu, duration, imgUrl} }) {

  function convertMinutesToHours(minutes) {
    if (typeof minutes !== 'number' || minutes < 0) {
      return 'Некорректное значение минут';
    }

    const hours = Math.floor(minutes / 60);
    const remainingMinutes = minutes % 60;

    const formattedHours = hours.toString().padStart(1, '0');
    const formattedMinutes = remainingMinutes.toString().padStart(2, '0');

    return `${formattedHours}ч ${formattedMinutes}м`;
  }

  const [isRemoveMovies, setRemoveMovies] = React.useState(false);
  const [isSavedMovie, setSavedMovies] = React.useState(false);

  const saveMovieToFavorites = () => {
    setSavedMovies(true);
  }

  const removeMovieToFavorites = () => {
    setRemoveMovies(true);
  }

  return (
    <li className="movies-card">
      <div className="movies-card__info">
        <h2 className="movies-card__title">{nameRu}</h2>
        <p className="movies-card__duration">{convertMinutesToHours(duration)}</p>
      </div>
      <img src={imgUrl} alt="Обложка фильма" className="movies-card__image" />
      <button className={`movies-card__btn ${isSavedMovie ? 'movies-card__btn_active' : isRemoveMovies ? 'movies-card__btn_remove' : ''}`} type="button" onClick={saveMovieToFavorites}></button>
    </li>
  )
}
