import React from "react";
// import Preloader from "../Preloader/Preloader.jsx";
import MoviesCard from "../MoviesCard/MoviesCard.jsx";

import {cards} from "../../utils/cards.js"


export default function MoviesCardList() {
  const [isRemoveMovies, setRemoveMovies] = React.useState(false);
  const [isSavedMovie, setSavedMovies] = React.useState(false);

  const saveMovieToFavorites = () => {
    setSavedMovies(true);
  }

  const removeMovieToFavorites = () => {
    setRemoveMovies(true);
  }

  return (
    <section className="movies-card-list">
      <ul className="movies-card-list__grid">
        {cards.map((card) => {
          return (
            <MoviesCard 
              key={card.movieId}
              card={card}
              isRemoveMovies={removeMovieToFavorites}
              isSavedMovie={saveMovieToFavorites}
            />
          );
        })}
      </ul>
      <button className="movies-card-list__more-btn" type="button">Ещё</button>
    </section>
  )
}
