import React from "react";
import FilterCheckbox from "../FilterCheckbox/FilterCheckbox";

export default function SearchForm() {

  const [isActiveCheckbox, setActiveCheckbox] = React.useState(false);

  const handleSubmit = (e) => {
    e.preventDefault();
  }

  const toggleCheckbox = () => {
    setActiveCheckbox();
  }
  return (
    <section className="search">
        <form noValidate className="search__form" onSubmit={handleSubmit} name="">
          <div className="search__input-wrapper">
          <input type="text" className="search__input" placeholder="Фильм" />
          <button className="search__btn">Поиск</button>
          </div>
          <span className="search__error">Введите текст запроса</span>
        </form>
        <FilterCheckbox onClick={toggleCheckbox} isActiveCheckbox={isActiveCheckbox}/>
    </section>
  )
}
