import React from "react";
import { Route, Routes } from 'react-router-dom';
import Header from "../Header/Header.jsx";
import Register from "../Register/Register.jsx";
import Login from "../Login/Login.jsx";
import Main from "../Main/Main.jsx";
import Movies from "../Movies/Movies.jsx";
import SavedMovies from "../SavedMovies/SavedMovies.jsx";
import Profile from "../Profile/Profile.jsx";
import PageNotFound from "../PageNotFound/PageNotFound.jsx";

export default function App() {

  const [isRemoveMovies, setRemoveMovies] = React.useState(false);
  const [isSavedMovie, setSavedMovies] = React.useState(false);

  const saveMovieToFavorites = () => {
    setSavedMovies(true);
  }

  const removeMovieToFavorites = () => {
    setRemoveMovies(true);
  }

  return (
    <Routes>
      <Route path="/" element={<Header />}>
        <Route index element={<Main />}/>
        <Route path="/movies" element={<Movies onClick={saveMovieToFavorites}/>} />
        <Route path="/saved-movies" element={<SavedMovies onClick={removeMovieToFavorites} />} />
        <Route path="/profile" element={<Profile />} />
      </Route>
      <Route path="/signin" element={<Login />} />
      <Route path="/signup" element={<Register />} />
      <Route path="/*" element={<PageNotFound />} />
    </Routes>
  )
}
