import React from "react";

const [isActiveCheckbox, setActiveCheckbox] = React.useState(false);

const toggleCheckbox = () => {
  setActiveCheckbox();
}

export default function FilterCheckbox() {

  return (
    <div className="filter-checkbox">
      <input type="checkbox" id="filter-movies" className="filter-checkbox__input" defaultChecked="isActiveCheckbox" />
      <label className="filter-checkbox__label" htmlFor="filter-movies">Короткометражки</label>
    </div>
  )
}
